import React, { useState, useEffect } from 'react';
import './Search.css';

const API_URL = 'http://localhost:5001';

function Search() {
  const [searchQuery, setSearchQuery] = useState('');
  const [candidates, setCandidates] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searched, setSearched] = useState(false);

  // Filters
  const [filters, setFilters] = useState({
    location: '',
    experience: [0, 15],
    skills: [],
    experienceLevel: [],
    education: [],
    workAuth: [],
    remote: []
  });

  const [skillInput, setSkillInput] = useState('');

  const handleSearch = async () => {
    if (!searchQuery.trim()) return;

    setLoading(true);
    setSearched(true);

    try {
      const response = await fetch(`${API_URL}/api/search`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          query: searchQuery,
          location: filters.location,
          filters: filters
        })
      });

      const data = await response.json();
      if (Array.isArray(data)) {
        setCandidates(data);
      }
    } catch (error) {
      console.error('Search error:', error);
    } finally {
      setLoading(false);
    }
  };

  const addSkill = () => {
    if (skillInput.trim() && !filters.skills.includes(skillInput.trim())) {
      setFilters(prev => ({
        ...prev,
        skills: [...prev.skills, skillInput.trim()]
      }));
      setSkillInput('');
    }
  };

  const removeSkill = (skill) => {
    setFilters(prev => ({
      ...prev,
      skills: prev.skills.filter(s => s !== skill)
    }));
  };

  const clearSearch = () => {
    setSearchQuery('');
    setCandidates([]);
    setSearched(false);
    setFilters({
      location: '',
      experience: [0, 15],
      skills: [],
      experienceLevel: [],
      education: [],
      workAuth: [],
      remote: []
    });
  };

  return (
    <div className="search-page">
      <div className="search-header">
        <h1>Find Perfect Candidates</h1>
        <p>Search through resumes with advanced AI-powered matching</p>
      </div>

      <div className="search-container">
        {/* Main Search */}
        <div className="search-main">
          <div className="search-input-group">
            <input
              type="text"
              placeholder="Search for candidates... (e.g., 'Python developer with 5 years experience', 'React specialist', 'Data scientist')"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
              className="search-input"
            />
            <button 
              onClick={handleSearch} 
              disabled={loading || !searchQuery.trim()}
              className="search-btn"
            >
              {loading ? 'Searching...' : 'Search'}
            </button>
            {searched && (
              <button onClick={clearSearch} className="clear-btn">
                Clear
              </button>
            )}
          </div>
        </div>

        {/* Filters */}
        <div className="filters-section">
          <h3>Filters</h3>

          <div className="filter-grid">
            {/* Location */}
            <div className="filter-group">
              <label>Location</label>
              <input
                type="text"
                placeholder="City, State, or Remote"
                value={filters.location}
                onChange={(e) => setFilters(prev => ({...prev, location: e.target.value}))}
                className="filter-input"
              />
            </div>

            {/* Skills */}
            <div className="filter-group">
              <label>Required Skills</label>
              <div className="skills-input">
                <input
                  type="text"
                  placeholder="Add skill (e.g., Python, React)"
                  value={skillInput}
                  onChange={(e) => setSkillInput(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && addSkill()}
                  className="filter-input"
                />
                <button onClick={addSkill} className="add-skill-btn">Add</button>
              </div>
              <div className="skills-tags">
                {filters.skills.map(skill => (
                  <span key={skill} className="skill-tag">
                    {skill}
                    <button onClick={() => removeSkill(skill)} className="remove-skill">Ã—</button>
                  </span>
                ))}
              </div>
            </div>

            {/* Experience Range */}
            <div className="filter-group">
              <label>Years of Experience: {filters.experience[0]} - {filters.experience[1]}+ years</label>
              <input
                type="range"
                min="0"
                max="15"
                value={filters.experience[1]}
                onChange={(e) => setFilters(prev => ({
                  ...prev, 
                  experience: [0, parseInt(e.target.value)]
                }))}
                className="range-input"
              />
            </div>
          </div>
        </div>

        {/* Results */}
        {searched && (
          <div className="search-results">
            <div className="results-header">
              <h3>Search Results ({candidates.length} candidates found)</h3>
            </div>

            {loading && (
              <div className="loading">
                <i className="fas fa-spinner fa-spin"></i>
                Searching candidates...
              </div>
            )}

            {!loading && candidates.length === 0 && (
              <div className="no-results">
                <i className="fas fa-search"></i>
                <p>No candidates found matching your criteria</p>
                <p>Try adjusting your search terms or filters</p>
              </div>
            )}

            {!loading && candidates.length > 0 && (
              <div className="candidates-grid">
                {candidates.map((candidate, index) => (
                  <div key={candidate.id || index} className="candidate-card">
                    <div className="candidate-header">
                      <h4>{candidate.name}</h4>
                      <span className="match-score">
                        {candidate.matchScore ? `${candidate.matchScore}% match` : ''}
                      </span>
                    </div>

                    <div className="candidate-info">
                      <p className="title">{candidate.title}</p>
                      {candidate.location && (
                        <p className="location">
                          <i className="fas fa-map-marker-alt"></i>
                          {candidate.location}
                        </p>
                      )}
                      {candidate.experience && (
                        <p className="experience">
                          <i className="fas fa-briefcase"></i>
                          {candidate.experience} years experience
                        </p>
                      )}
                      {candidate.email && (
                        <p className="email">
                          <i className="fas fa-envelope"></i>
                          {candidate.email}
                        </p>
                      )}
                    </div>

                    {candidate.skills && candidate.skills.length > 0 && (
                      <div className="candidate-skills">
                        {candidate.skills.slice(0, 5).map((skill, idx) => (
                          <span key={idx} className="skill-badge">{skill}</span>
                        ))}
                        {candidate.skills.length > 5 && (
                          <span className="skill-badge">+{candidate.skills.length - 5}</span>
                        )}
                      </div>
                    )}

                    {candidate.summary && (
                      <div className="candidate-summary">
                        <p>{candidate.summary.substring(0, 150)}...</p>
                      </div>
                    )}

                    <div className="candidate-actions">
                      <button className="action-btn primary">
                        <i className="fas fa-eye"></i>
                        View Profile
                      </button>
                      <button className="action-btn secondary">
                        <i className="fas fa-star"></i>
                        Shortlist
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {/* Search Tips */}
        {!searched && (
          <div className="search-tips">
            <h3>Search Tips</h3>
            <div className="tips-grid">
              <div className="tip">
                <i className="fas fa-lightbulb"></i>
                <h4>Be Specific</h4>
                <p>Try "Senior React developer with Node.js experience" instead of just "developer"</p>
              </div>
              <div className="tip">
                <i className="fas fa-filter"></i>
                <h4>Use Filters</h4>
                <p>Combine search terms with location, experience level, and skill filters</p>
              </div>
              <div className="tip">
                <i className="fas fa-search-plus"></i>
                <h4>Try Different Terms</h4>
                <p>Search for both "JavaScript" and "JS", "Machine Learning" and "ML"</p>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Search;
